using Android.App;using Android.Widget;using Android.OS;using System;namespace TipCalculator {    [Activity(Label = "TipCalculator", MainLauncher = true)]    public class MainActivity : Activity {        Button btnCalculate;        EditText etTotal;        TextView tvTipAmount;        TextView tvTotal;        protected override void OnCreate(Bundle savedInstanceState) {            base.OnCreate(savedInstanceState);            SetContentView(Resource.Layout.Main);            btnCalculate = FindViewById<Button>(Resource.Id.btnSubmit);            etTotal = FindViewById<EditText>(Resource.Id.inputBill);            tvTipAmount = FindViewById<TextView>(Resource.Id.tipAmount);            tvTotal = FindViewById<TextView>(Resource.Id.totalAmount);            btnCalculate.Click += CalculateTip;        }        void CalculateTip(object sender, EventArgs e) {            double total = double.Parse(etTotal.Text);            double tip = total * .15;            tvTotal.Text = (total + tip).ToString();            tvTipAmount.Text = tip.ToString();        }    }}